SELECT IFNULL(id,0) AS id, IFNULL(capitalflowid,'')AS capitalflowid, IFNULL(title,'') AS title, IFNULL(report_person,'') AS report_person, IFNULL(report_quarter,'') AS report_quarter, IFNULL(report_text,'') AS report_text, IFNULL(report_attachment,'')AS report_attachment, IFNULL(guihuakeshenqingperson,'') AS guihuakeshenqingperson, IFNULL(create_time,'') AS create_time, IFNULL(initiatorclass,'') AS initiatorclass, IFNULL(status,'') AS status, IFNULL(money_source,'') AS money_source, IFNULL(arrival_time,'') AS arrival_time, IFNULL(amount,'') AS amount, IFNULL(caiwuchuliren,'') AS caiwuchuliren, IFNULL(finance_time,'') AS finance_time, IFNULL(caiwuattachment,'') AS caiwuattachment, IFNULL(areaname,'') AS areaname, IFNULL(areanamedelete,'') AS areanamedelete, IFNULL(text,'') AS text, IFNULL(guihuachuliren,'') AS guihuachuliren,
IFNULL(toarea_time,'') AS toarea_time,
IFNULL(titlequxian,'') AS titlequxian, IFNULL(shenqingren,'') AS shenqingren, IFNULL(report_reason,'') AS report_reason, IFNULL(quxianattachment,'') AS quxianattachment, IFNULL(quxianshenqingren,'') AS quxianshenqingren, IFNULL(quxianshenqingtime,'') AS quxianshenqingtime, IFNULL(replytext,'') AS replytext, IFNULL(guihuapifuren,'') AS guihuapifuren, IFNULL(guihuapifutime,'') AS guihuapifutime, IFNULL(dealtext,'') AS dealtext, IFNULL(caiwuzhuanzhangren,'') AS caiwuzhuanzhangren, IFNULL(caiwuzhuangzhangtime,'') AS caiwuzhuangzhangtime, IFNULL(capitalflowinstruction,'') AS capitalflowinstruction, IFNULL(quxianbaocunren,'') AS quxianbaocunren, IFNULL(quxianbaocuntime,'') AS quxianbaocuntime, IFNULL(quxiantijiaoren,'') AS quxiantijiaoren, IFNULL(quxiantijiaotime,'') AS quxiantijiaotime FROM capitalflow WHERE capitalflowid = #{id}
